<?php
namespace LTPet\Preview;

use pocketmine\Player;
use pocketmine\entity\Entity;
use pocketmine\item\Item;
use pocketmine\network\protocol\AddEntityPacket;
use pocketmine\network\protocol\RemoveEntityPacket;
use pocketmine\network\protocol\MoveEntityPacket;
use pocketmine\network\protocol\LevelEventPacket;
use pocketmine\network\protocol\MovePlayerPacket;
use pocketmine\network\protocol\AddPlayerPacket;
use pocketmine\network\protocol\PlayerListPacket;
use pocketmine\utils\UUID;
use pocketmine\math\Vector3;
use LTPet\Pets\WalkingPets\{
	LTRabbit,LTChicken,LTPig,LTSheep,LTWolf,LTVillager,LTOcelot,LTHorse,LTSilverfish,LTSlime,LTCreeper,LTNPC,LTLlama,LTSpider,LTSkeleton
};
use LTPet\Pets\FlyingPets\{
	LTWitherBoss,LTEnderDragon
};
use LTPet\Pet;

class PreviewPet extends Vector3{
	const LTRabbit=0;//兔子
	const LTChicken=1;//鸡
	const LTPig=2;//猪
	const LTSheep=3;//羊
	const LTWolf=4;//狼
	const LTVillager=5;//村民
	const LTOcelot=6;//豹猫
	const LTHorse=7;//马
	const LTWitherBoss=8;//凋零
	const LTEnderDragon=9;//末影龙
	const LTSilverfish=10;//蠢虫
	const LTSlime=11;//史莱姆
	const LTCreeper=12;//苦力怕
	const LTLlama=13;//草泥马
	const LTSpider=14;//蜘蛛
	const LTSkeleton=15;//骷髅
	const LTNPC=16;//NPC
	
	public static $PreviewPets=[];
	
	private $Position;
	private $ID;
	private $entityID;
	private $eid;
	private $height;
	private $UUID;
	private $need;
	private $info;
	private $name;
	public static function init(){
		self::$PreviewPets[self::LTRabbit]=new PreviewPet(self::LTRabbit, LTRabbit::NETWORK_ID, '兔子', 0);
		self::$PreviewPets[self::LTChicken]=new PreviewPet(self::LTChicken, LTChicken::NETWORK_ID, '鸡', 0);
		self::$PreviewPets[self::LTPig]=new PreviewPet(self::LTPig, LTPig::NETWORK_ID, '猪', 0);
		self::$PreviewPets[self::LTSheep]=new PreviewPet(self::LTSheep, LTSheep::NETWORK_ID, '羊', 0);
		self::$PreviewPets[self::LTWolf]=new PreviewPet(self::LTWolf, LTWolf::NETWORK_ID, '狼', 0);
		self::$PreviewPets[self::LTVillager]=new PreviewPet(self::LTVillager, LTVillager::NETWORK_ID, '村民', 0);
		self::$PreviewPets[self::LTOcelot]=new PreviewPet(self::LTOcelot, LTOcelot::NETWORK_ID, '豹猫', 0);
		self::$PreviewPets[self::LTHorse]=new PreviewPet(self::LTHorse, LTHorse::NETWORK_ID, '马', 0);
		self::$PreviewPets[self::LTWitherBoss]=new PreviewPet(self::LTWitherBoss, LTWitherBoss::NETWORK_ID , '凋零', 0);
		self::$PreviewPets[self::LTEnderDragon]=new PreviewPet(self::LTEnderDragon, LTEnderDragon::NETWORK_ID, '末影龙', 1.5);
		self::$PreviewPets[self::LTSilverfish]=new PreviewPet(self::LTSilverfish, LTSilverfish::NETWORK_ID, '蠢虫', 0);
		self::$PreviewPets[self::LTSlime]=new PreviewPet(self::LTSlime, LTSlime::NETWORK_ID, '史莱姆', 0);
		self::$PreviewPets[self::LTCreeper]=new PreviewPet(self::LTCreeper, LTCreeper::NETWORK_ID, '苦力怕', 0);
		self::$PreviewPets[self::LTLlama]=new PreviewPet(self::LTLlama, LTLlama::NETWORK_ID, '羊驼', 0);
		self::$PreviewPets[self::LTSpider]=new PreviewPet(self::LTSpider, LTSpider::NETWORK_ID, '蜘蛛', 0);
		self::$PreviewPets[self::LTSkeleton]=new PreviewPet(self::LTSkeleton, LTSkeleton::NETWORK_ID, '骷髅', 0);
		self::$PreviewPets[self::LTNPC]=new PreviewPet(self::LTNPC, LTNPC::NETWORK_ID, '女仆', 1.62);
	}
	public static function get($id){
		return self::$PreviewPets[$id]??null;
	}
	public function __construct($ID, $entityID, $name, $height=0){
		$this->ID=$ID;
		$this->height=$height;
		$this->entityID=$entityID;
		$this->name=$name;
		$this->eid=Entity::$entityCount++;
		$this->Position=new Vector3(740.5, 4+$height, 60.5);
		$this->need=Pet::getCount($this->getName());
		if($this->need===false){
			$this->info='§l§d限活动获得！';
		}else{
			$this->info='§l§d '.$this->getName().'§e需要：§l§c材料'.$this->getNeed()[0].' '.$this->getNeed()[1].'个';
		}
		if($this->getID()==self::LTNPC){
			$this->UUID=UUID::fromRandom();
		}
		parent::__construct($this->Position->getX(), $this->Position->getY(), $this->Position->getZ());
	}
	public static function getMax(){
		return count(self::$PreviewPets)-1;
	}
	public function getEid(){
		return $this->eid;
	}
	public function getName(){
		return $this->name;
	}
	public function getNeed(){
		return $this->need;
	}
	public function getID(){
		return $this->ID;
	}
	public function getEntityID(){
		return $this->entityID;
	}
	public function spawnTo(Player $player){
		$x=$this->getX()-$player->getX();
		$y=$this->getY()-$player->getY();
		$z=$this->getZ()-$player->getZ();
		$yaw=asin($x/sqrt($x*$x+$z*$z))/3.14*180;
		$pitch=round(asin($y/sqrt($x*$x+$z*$z+$y*$y))/3.14*180);
		if($z>0){
			$yaw=-$yaw+180;
		}
		$flags = 0;
		$flags |= 1 << Entity::DATA_FLAG_CAN_SHOW_NAMETAG;
		$flags |= 1 << Entity::DATA_FLAG_ALWAYS_SHOW_NAMETAG;
		$flags |= 1 << Entity::DATA_FLAG_IMMOBILE;
		$metadata = [
			Entity::DATA_FLAGS => [Entity::DATA_TYPE_LONG, $flags],
			Entity::DATA_AIR => [Entity::DATA_TYPE_SHORT, 400],
			Entity::DATA_NAMETAG => [Entity::DATA_TYPE_STRING, $this->info],
			Entity::DATA_LEAD_HOLDER_EID => [Entity::DATA_TYPE_LONG, -1],
		];  
		if($this->getID()===self::LTNPC){//NPC
			$pk = new AddPlayerPacket();
			$pk->uuid = $this->UUID;
			$pk->username = 'test';
			$pk->item = Item::get(276);
			$pk->y = $this->getY()-$this->height;
		}else{
			$pk = new AddEntityPacket();
			$pk->type = $this->getEntityID();
			$pk->y = $this->getY();
		}
		$pk->metadata = $metadata;
		if($this->getName()=='末影龙'){
			if($yaw > 0){
				$yaw=$yaw - 180;
			}elseif($yaw < 0){
				$yaw=$yaw + 180;
			}
			$pk->metadata[Entity::DATA_SCALE] = [Entity::DATA_TYPE_FLOAT, 0.3];
		}
		$pk->yaw = $yaw;
		$pk->pitch = $pitch;
		$pk->eid = $this->eid;
		$pk->x = $this->getX();
		$pk->z = $this->getZ();
		$pk->speedX = 0;
		$pk->speedY = 0;
		$pk->speedZ = 0;
		$pk->pitch = $pitch;
		$player->dataPacket($pk);
		if($this->getID()===self::LTNPC){
			$pk = new PlayerListPacket();
			$pk->type = PlayerListPacket::TYPE_ADD;
			$pk->entries[] = [$this->UUID, $this->eid, '§d预览女仆', 'Standard_CustomSlim', ''];
			$player->dataPacket($pk);
		}
	}
	public function despawnFrom(Player $player){
		if($this->getID()===self::LTNPC)$player->getServer()->removePlayerListData($this->UUID,[$player]);
		$pk=new RemoveEntityPacket();
		$pk->eid=$this->eid;
		$player->dataPacket($pk);
	}
	public function updateSee(Player $player){//更新实体看的方向
		if($player->distance($this)>10)return true;
		$x=$this->getX()-$player->getX();
		$y=($this->getID()===self::LTNPC?$this->getY()-$this->height:$this->getY())-$player->getY();
		$z=$this->getZ()-$player->getZ();
		$yaw=asin($x/sqrt($x*$x+$z*$z))/3.14*180;
		$pitch=round(asin($y/sqrt($x*$x+$z*$z+$y*$y))/3.14*180);
		if($z>0){
			$yaw=-$yaw+180;
		}
		if($this->getID()===self::LTNPC){//NPC
			$pk=new MovePlayerPacket();
			$pk->bodyYaw=$yaw;
		}else{
			$pk=new MoveEntityPacket();
			if($this->name=='末影龙'){
				if($yaw > 0){
					$yaw=$yaw - 180;
				}elseif($yaw < 0){
					$yaw=$yaw + 180; 
				}
			}
			$pk->headYaw=$yaw;
		}
		$pk->eid=$this->eid;
		$pk->pitch=$pitch;
		$pk->yaw=$yaw;
		$pk->x=$this->getX();
		$pk->y=$this->getY();
		$pk->z=$this->getZ();
		$player->dataPacket($pk);
		return true;
	}
}